/**
 *
 * MovieDetailPage
 *
 */

import React, { memo, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Helmet } from 'react-helmet';
import { createStructuredSelector } from 'reselect';
import { compose } from 'redux';

import Spin from 'components/LoadingIndicator';
import { Row, Col, Tag, Typography, Card, Image } from 'antd';

import { useInjectSaga } from 'utils/injectSaga';
import { useInjectReducer } from 'utils/injectReducer';
import { fetchMovieById } from './actions';
import makeSelectMovieDetailPage from './selectors';
import reducer from './reducer';
import saga from './saga';
import { IMAGE_BASE_URL } from '../../utils/constants';

export function MovieDetailPage(props) {
  useInjectReducer({ key: 'movieDetailPage', reducer });
  useInjectSaga({ key: 'movieDetailPage', saga });

  const { Text, Title } = Typography;

  const { onGetMovie, movieDetailPage, match } = props;
  const { id } = match.params;
  const { details, loading, error } = movieDetailPage;

  useEffect(() => {
    onGetMovie(id);
  }, []);

  return (
    <div>
      <Helmet>
        <title>MovieDetailPage</title>
        <meta name="description" content="Selected movie details" />
      </Helmet>
      <div className="main">
        <Row justify="center" style={{ marginTop: '25px' }}>
          {loading && <Spin />}
          {!loading && error && <Text type="danger">{error}</Text>}
          {!loading && details && (
            <Card
              style={{ width: '500px' }}
              cover={
                <Image
                  alt={details.title}
                  preview={false}
                  src={IMAGE_BASE_URL + details.backdrop_path}
                  fallback="data:image/png;base64,"
                />
              }
            >
              <Row>
                <Col>
                  <Title level={4}>{details.title}</Title>
                </Col>
              </Row>
              <Row>
                <Col>
                  <Text>{details.overview}</Text>
                </Col>
              </Row>
              <Row>
                <Col>
                  Genre:{' '}
                  {details.genres &&
                    details.genres.map(genre => <Tag>{genre.name}</Tag>)}
                </Col>
              </Row>
              <Row>
                <Col>
                  Status: <Text>{details.status}</Text>
                </Col>
              </Row>
              <Row>
                <Col>
                  Release date: <Text>{details.release_date}</Text>
                </Col>
              </Row>
              <Row>
                <Col>
                  Runtime: <Text>{details.runtime}</Text>
                </Col>
              </Row>
              <Row>
                <Col>
                  Popularity: <Text>{details.popularity}</Text>
                </Col>
              </Row>
            </Card>
          )}
        </Row>
      </div>
    </div>
  );
}

MovieDetailPage.propTypes = {
  onGetMovie: PropTypes.func,
  movieDetailPage: PropTypes.object,
  match: PropTypes.string,
};

const mapStateToProps = createStructuredSelector({
  movieDetailPage: makeSelectMovieDetailPage(),
});

function mapDispatchToProps(dispatch) {
  return {
    onGetMovie: payload => dispatch(fetchMovieById(payload)),
  };
}

const withConnect = connect(
  mapStateToProps,
  mapDispatchToProps,
);

export default compose(
  withConnect,
  memo,
)(MovieDetailPage);
